// src/pages/LoginPage.js
import React, { useState } from "react";
import axios from "axios";

// Set base URL for Axios
axios.defaults.baseURL = "http://localhost:3001";
axios.defaults.headers.post["Content-Type"] = "application/json";

const LoginPage = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [debugMessage, setDebugMessage] = useState("");

    // Handle the actual login via form submission
    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("/auth/login", { username, password });
            alert(response.data.message); // Success message
            localStorage.setItem("token", response.data.token); // Store token
            setError(""); // Clear error on success
        } catch (err) {
            console.error("Error details:", err); // Log full error
            setError(err.response?.data?.message || "An error occurred"); // Show error
        }
    };

    // Test Login with hardcoded credentials
    const handleTest = async () => {
        try {
            const response = await axios.post("/auth/login", {
                username: "admin",
                password: "password",
            });
            console.log("Response:", response.data);
            setDebugMessage(response.data.message); // Show debug success message
        } catch (err) {
            console.error("Error during test login:", err);
            setDebugMessage("Test login failed. Check console for details.");
        }
    };

    return (
        <div style={{ padding: "20px" }}>
            <h1>Login Page</h1>
            <form onSubmit={handleLogin}>
                <div>
                    <label>Username:</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>
                {error && <p style={{ color: "red" }}>{error}</p>}
                <button type="submit">Login</button>
            </form>

            <hr />

            <h2>Debug Test</h2>
            <button onClick={handleTest}>Test Login with Admin Credentials</button>
            {debugMessage && <p>{debugMessage}</p>}
        </div>
    );
};

export default LoginPage;
